import React, { useState, useContext } from 'react';
import { UserContext } from '../userContext';

function Listing() {
  const userContext = useContext(UserContext);
  const [content, setContent] = useState('');
  const [photos, setPhotos] = useState([]);
  const [location, setLocation] = useState('');

  const { user } = userContext;

  const handleCreateListing = async () => {
    console.log("created");
    try {
      // Create a new FormData object to store the form data
      const formData = new FormData();
      formData.append('title', content); // Assuming content corresponds to the title field
      formData.append('location', location); // Add the location to the form data

      // Append each selected photo to the form data
      photos.forEach((photo) => {
        formData.append('photos', photo);
      });
      console.log(photos);

      //formData.append('price', 0);       
      formData.append('postedBy', user._id); // Append the ID of the logged-in user to the form data

      const response = await fetch('http://localhost:3001/listings', {
        method: 'POST',
        body: formData, // Send the form data as the request body
      });

      if (response.ok) {
        const listing = await response.json();
        console.log('Listing created:', listing);
        // Perform any necessary actions after successful listing creation
      } else {
        console.error('Error creating listing:', response.status);
      }
    } catch (error) {
      console.error('Error creating listing:', error);
    }
  };

  const handleContentChange = (event) => {
    setContent(event.target.value);
  };

  const handlePhotosChange = (event) => {
    // Handle multiple file uploads and store the selected photos
    const selectedPhotos = Array.from(event.target.files);
    setPhotos(selectedPhotos);
  };

  const handleLocationChange = (event) => {
    setLocation(event.target.value);
  };

  return (
    <div>
      <h3>Create Listing</h3>
      <form>
        <div>
          <label htmlFor="content">Content:</label>
          <textarea id="content" value={content} onChange={handleContentChange} />
        </div>
        <div>
          <label htmlFor="photos">Photos:</label>
          <input type="file" id="photos" multiple onChange={handlePhotosChange} />
        </div>
        <div>
          <label htmlFor="location">Location:</label>
          <input type="text" id="location" value={location} onChange={handleLocationChange} />
        </div>
        <button type="button" onClick={handleCreateListing}>Create Listing</button>
      </form>
    </div>
  );
}

export default Listing;
