import React, { useState, useEffect, useContext } from 'react';
import { UserContext } from '../userContext';
import { useParams, useNavigate } from 'react-router-dom';
import ListingShow from './ListingShow';

function ListingDetails() {
  const { id } = useParams(); // Retrieve the listing ID from the URL parameters
  const [listing, setListing] = useState(null);
  const userContext = useContext(UserContext);
  const { user } = userContext;
  const navigate = useNavigate();

  useEffect(() => {
    const fetchListing = async () => {
      try {
        const response = await fetch(`http://localhost:3001/listings/${id}`, {
          method: 'GET',
          credentials: 'include', // Include cookies in the request
        });
        if (response.ok) {
          const data = await response.json();
          setListing(data); // Update the listing state with the fetched data
        } else {
          console.error('Failed to fetch listing:', response.status);
          // Handle the error or show an error message to the user
        }
      } catch (error) {
        console.error('Error fetching listing:', error);
        // Handle the error or show an error message to the user
      }
    };

    fetchListing();
  }, [id]);

  const handleBookListing = async () => {
    try {
      const response = await fetch(`http://localhost:3001/listings/${id}/book`, {
        method: 'POST',
        credentials: 'include', // Include cookies in the request
      });
      if (response.ok) {
        // Handle successful booking
        navigate('/confirmation'); // Redirect to the booking confirmation page
      } else {
        console.error('Error booking listing:', response.status);
        // Handle the error or show an error message to the user
      }
    } catch (error) {
      console.error('Error booking listing:', error);
      // Handle the error or show an error message to the user
    }
  };

  if (!listing) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      <h3>Listing Details</h3>
      <div>
        <p>Title: {listing.title}</p>
        <p>Description: {listing.description}</p>
        <p>Price: {listing.price}</p>
        <p>Location: {listing.location}</p>
        <p>Posted By: {listing.postedBy.username}</p>
        <p>Email: {listing.postedBy.email}</p>
        <div className="listing-photos">
          {listing.photos.map((photo) => (
            <ListingShow
              key={photo._id}
              photoPath={photo.photoPath}
              alt="Listing Photo"
            />
          ))}
        </div>
        <button type="button" className="btn btn-primary" onClick={handleBookListing}>
          Book Listing
        </button>
      </div>
    </div>
  );
}

export default ListingDetails;
