import { useContext, useState } from 'react'
import { Navigate } from 'react-router';
import { UserContext } from '../userContext';

function AddPhoto(props) {
    const userContext = useContext(UserContext);
    const [name, setName] = useState('');
    const [file, setFile] = useState('');
    const [uploaded, setUploaded] = useState(false);

    const [tags, setTags] = useState('');

    const handleTagChange = (event) => {
        setTags(event.target.value);
    };

    async function onSubmit(e) {
        e.preventDefault();

        if (!name) {
            alert("Vnesite ime!");
            return;
        }

        const formData = new FormData();
        formData.append('name', name);
        formData.append('photoPath', file);
        // Split the tags string into an array
        const tagArray = tags.split(',');
        tagArray.forEach((tag) => {
            formData.append('tags', tag.trim()); // Trim any leading/trailing whitespace
        }); const res = await fetch('http://localhost:3001/photos', {
            method: 'POST',
            credentials: 'include',
            body: formData
        });
        const data = await res.json();

        setUploaded(true);
    }

    return (
        <form className="form-group" onSubmit={onSubmit}>
            {!userContext.user ? <Navigate replace to="/login" /> : ""}
            {uploaded ? <Navigate replace to="/" /> : ""}
            <input type="text" className="form-control" name="ime" placeholder="Ime slike" value={name} onChange={(e) => { setName(e.target.value) }} />
            <label>Izberi sliko</label>
            <input type="file" id="file" onChange={(e) => { setFile(e.target.files[0]) }} />
            <input type="text" value={tags} onChange={handleTagChange} placeholder="Enter tags (comma-separated)" />
            <input className="btn btn-primary" type="submit" name="submit" value="Naloži" />
        </form>
    )
}

export default AddPhoto;
