import React, { useState, useEffect } from 'react';
import Photo from './Photo';
import TagSearch from './TagSearch';

function Photos() {
  const [photos, setPhotos] = useState([]);
  const [searchResults, setSearchResults] = useState([]);
  const [filteredPhotos, setFilteredPhotos] = useState([]);

  useEffect(() => {
    const fetchPhotos = async () => {
      try {
        const res = await fetch('http://localhost:3001/photos');
        const data = await res.json();
        const filteredData = data.filter(photo => photo.dislikes < 2);
        setPhotos(filteredData);
        setFilteredPhotos(filteredData);
      } catch (error) {
        console.error('Error fetching photos:', error);
      }
    };
    fetchPhotos();
  }, []);

  const handleTagSearch = (searchTerm) => {
    // Filter the photos based on the search term
    const results = photos.filter(
      (photo) => photo.tags && photo.tags.includes(searchTerm.toLowerCase())
    );
    setSearchResults(results);
  };

  const photosToRender = searchResults.length > 0 ? searchResults : filteredPhotos;

  return (
    <div>
      <h3>Photos:</h3>
      <TagSearch onSearch={handleTagSearch} />
      <ul>
        {photosToRender.map((photo) => (
          <Photo photo={photo} key={photo._id} />
        ))}
      </ul>
    </div>
  );
}

export default Photos;
