import React, { useState, useEffect } from 'react';

function Comment({ comment }) {
  const [author, setAuthor] = useState('');
console.log(comment.postedBy);
console.log("ssf");
  useEffect(() => {
    // Fetch the author data for the comment
    fetchAuthorData(comment.postedBy)
      .then((authorData) => {
        // Update the author state with the fetched author data
        setAuthor(authorData.username);
      })
      .catch((error) => {
        console.error('Error fetching author data:', error);
      });
  }, [comment.postedBy]);

  const fetchAuthorData = async (userId) => {
    try {
      const res = await fetch(`http://localhost:3001/users/${userId}`);
      const data = await res.json();
      return data;
    } catch (error) {
      throw new Error('Failed to fetch author data');
    }
  };

  return (
    <div className="comment">
      <p className="comment-content">{comment.content}</p>
      <p className="comment-author">Posted by: {author}</p>
    </div>
  );
}

export default Comment;
