import React, { useState, useEffect, useContext } from 'react';
import { UserContext } from '../userContext';
import ListingShow from './ListingShow';

function Index() {
  const [listings, setListings] = useState([]);
  const userContext = useContext(UserContext);
  const { user } = userContext;

  useEffect(() => {
    const fetchListings = async () => {
      try {
        const response = await fetch(`http://localhost:3001/listings/`, {
          method: 'GET',
          credentials: 'include', // Include cookies in the request
        }); 
        if (response.ok) {
          const data = await response.json();
          setListings(data); // Update the listings state with the fetched data
        } else {
          console.error('Failed to fetch listings:', response.status);
          // Handle the error or show an error message to the user
        }
      } catch (error) {
        console.error('Error fetching listings:', error);
        // Handle the error or show an error message to the user
      }
    };

    fetchListings();
  }, []);


  return (
    <div>
      <h3>Here are all avalible listings:</h3>
      <ul>
        {listings.map((listing) => (
          <div>

          <ListingShow
            listing={listing}
            key={listing._id}
            onBook={() => ListingShow.handleBookListing(listing._id)}
            onTakeDown={() => ListingShow.handleTakeDownListing(listing._id)}
            currentUser={user}
            />
          <img  src="gttp://localhost:3001/images/</div>{listing.photos.photoPath}"></img>
            </div>
        ))}
      </ul>
    </div>
  );
}

export default Index;
