import React, { useState, useEffect } from 'react';
import Listing from './Listing';

function Index() {
  const [listings, setListings] = useState([]);

  useEffect(() => {
    const fetchListings = async () => {
      try {
        const response = await fetch('http://localhost:3001/listings');
        if (response.ok) {
          const data = await response.json();
          setListings(data); // Update the listings state with the fetched data
          console.log(data); // Log the listings to the console
        } else {
          console.error('Failed to fetch listings:', response.status);
          // Handle the error or show an error message to the user
        }
      } catch (error) {
        console.error('Error fetching listings:', error);
        // Handle the error or show an error message to the user
      }
    };

    fetchListings();
  }, []);

  useEffect(() => {
    const fetchListingPhotos = async (listingId) => {
      try {
        const response = await fetch(`http://localhost:3001/listings/${listingId}/photos`);
        if (response.ok) {
          const photos = await response.json();
          // Handle the received photos data
          console.log(photos); // Example: Log the photos to the console
        } else {
          console.error('Failed to fetch listing photos:', response.status);
          // Handle the error or show an error message to the user
        }
      } catch (error) {
        console.error('Error fetching listing photos:', error);
        // Handle the error or show an error message to the user
      }
    };

    listings.forEach((listing) => {
      console.log("run");
      fetchListingPhotos(listing._id);
    });
  }, [listings]);

  return (
    <div>
      <h3>Listings:</h3>
      <ul>
        {listings.map((listing) => (
          <Listing listing={listing} key={listing._id} />
        ))}
      </ul>
    </div>
  );
}

export default Index;
