import React, { useState, useEffect } from 'react';
import Listing from './Listing';

function Index() {
  const [listings, setListings] = useState([]);

  useEffect(() => {
    const fetchListings = async () => {
      try {
        const response = await fetch('http://localhost:3001/listings');
        if (response.ok) {
          const data = await response.json();
          setListings(data); // Update the listings state with the fetched data
          console.log(data); // Log the listings to the console
        } else {
          console.error('Failed to fetch listings:', response.status);
          // Handle the error or show an error message to the user
        }
      } catch (error) {
        console.error('Error fetching listings:', error);
        // Handle the error or show an error message to the user
      }
    };

    fetchListings();
  }, []);

  const fetchPhoto = async (photoId) => {
    try {
      const response = await fetch(`http://localhost:3001/listings/${photoId}/`);
      if (response.ok) {
        const photo = await response.json();
        return photo;
      } else {
        console.error('Failed to fetch photo:', response.status);
        // Handle the error or show an error message to the user
      }
    } catch (error) {
      console.error('Error fetching photo:', error);
      // Handle the error or show an error message to the user
    }
  };

  return (
    <div>
      <h3>Listings:</h3>
      <ul>
        {listings.map(async (listing) => {
          const photo = await fetchPhoto(listing.photo);
          return (
            <div key={listing._id}>
              <h4>Title: {listing.title}</h4>
              <p>Location: {listing.location}</p>
              {photo && (
                <div>
                  <h4>Photo:</h4>
                  <img src={photo.photoPath} alt="Listing Photo" />
                </div>
              )}
              <br />
            </div>
          );
        })}
      </ul>
    </div>
  );
}

export default Index;
