    const res = await fetch('http://localhost:3001/comments', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        photoId: props.photo._id,
        content: comment,
        postedBy: user._id,
      }),
    });

    if (res.ok) {
      // Comment created successfully, perform any necessary actions
      // such as updating the comments list or refreshing the photo display
      // ...

      // Reset the comment input field
      setComment('');
    } else {
      // Handle error if the comment creation fails
      // ...
    }
  };

