import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

function ListingShow({ listing, currentUser }) {
  const [photos, setPhotos] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchListingPhotos = async () => {
      try {
        const response = await fetch(`http://localhost:3001/listings/${listingId}/photos`, {
          method: 'GET',
          credentials: 'include', // Include cookies in the request
        });
        if (response.ok) {
          const photosData = await response.json();
          setPhotos(photosData);
        } else {
          console.error('Failed to fetch listing photos:', response.status);
          // Handle the error or show an error message to the user
        }
      } catch (error) {
        console.error('Error fetching listing photos:', error);
        // Handle the error or show an error message to the user
      }
    };

    fetchListingPhotos();
  }, [id]);

  const handleBookListing = () => {
    navigate(`/detailed/${listing._id}`);
  };

  const handleTakeDownListing = async () => {
    // ... your existing code for taking down the listing
  };

  return (
    <div className="card">
      <div className="card-body">
        <h4 className="card-title">Title: {listing.title}</h4>
        <p className="card-text">Location: {listing.location}</p>
        {photos.length > 0 && (
          <div>
            <h4>Photos:</h4>
            {photos.map((photo) => (
              <img src={`http://localhost:3001/images/${photo.photoData}`} alt="Listing Photo" />
            ))}
          </div>
        )}
        {currentUser && currentUser._id !== listing.postedBy && !listing.booked && (
          <button className="btn btn-primary" onClick={handleBookListing}>
            Closer look
          </button>
        )}
        {currentUser && currentUser._id === listing.postedBy && (
          <button className="btn btn-danger" onClick={handleTakeDownListing}>
            Take Down Listing
          </button>
        )}
      </div>
    </div>
  );
}

export default ListingShow;
