import React, { useState, useContext, useEffect } from 'react';
import Comments from './Comments';
import { UserContext } from '../userContext';
import TagSearch from './TagSearch';

function Photo(props) {
  const [comment, setComment] = useState('');
  const [isDetailView, setIsDetailView] = useState(false);
  const userContext = useContext(UserContext);
  const [photoData, setPhotoData] = useState([]);
  const [searchResults, setSearchResults] = useState([]);


  const handleTagSearch = (searchTerm) => {
    // Filter the photos based on the search term
    const results = photos.filter((photo) =>
      photo.tags.includes(searchTerm.toLowerCase())
    );
    setSearchResults(results);
  };

  const { user } = userContext;

  console.log(user);

  useEffect(() => {
    // Fetch the photo data and update the state
    fetchPhotoData();
  }, []);

  const fetchPhotoData = async () => {
    try {
      const res = await fetch('http://localhost:3001/photos');
      const data = await res.json();
      setPhotoData(data);
    } catch (error) {
      console.error('Error fetching photo data:', error);
    }
  };

  const handleLike = async (photoId) => {
    try {
      if (!user) {
        console.log('User is not logged in.');
        return;
      }
      await fetch(`http://localhost:3001/photos/${photoId}/like`, {
        method: 'POST',
        credentials: 'include',
      });

      // Perform a separate request to get the updated photo data
      const updatedPhotoRes = await fetch(`http://localhost:3001/photos/${photoId}`);
      const updatedPhoto = await updatedPhotoRes.json();

      // Update the photo data with the updated likes
      const updatedPhotoData = photoData.map((photo) => (photo._id === updatedPhoto._id ? updatedPhoto : photo));
      setPhotoData(updatedPhotoData);
    } catch (error) {
      console.error('Error liking photo:', error);
    }
  };

  const toggleDetailView = () => {
    setIsDetailView(!isDetailView);
  };

  const handleCommentChange = (event) => {
    setComment(event.target.value);
  };

  const handleCommentSubmit = async (event) => {
    event.preventDefault();

    if (!user) {
      console.log('User not logged in');
      return;
    }

    if (!comment) {
      console.log('Comment field cannot be empty');
      return;
    }

    // Make API request to create a new comment
    const res = await fetch('http://localhost:3001/comments', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        photoId: props.photo._id,
        content: comment,
        postedBy: user._id,
      }),
    });

    if (res.ok) {
      // Comment created successfully, perform any necessary actions
      // such as updating the comments list or refreshing the photo display
      // ...

      // Reset the comment input field
      setComment('');
    } else {
      console.log('Error: Comment creation failed');
      // Handle error if the comment creation fails
      // ...
    }
  };

  const publishTime = new Date(props.photo.publishTime).toLocaleString([], {
    year: 'numeric',
    month: 'short',
    day: 'numeric',
    hour: '2-digit',
    minute: '2-digit',
    second: '2-digit',
  });

  return (
    <div className="card text-dark mb-4">
      <div className="card-body">
        <h5 className="card-title">Title: {props.photo.name}</h5>
        <p className="card-text">Posted by: {props.photo.postedBy.username}</p>
        <p className="card-text">Published at: {publishTime}</p>
        <p className="card-text">Likes: {props.photo.likes}</p>

        {user && (
          <button onClick={() => handleLike(props.photo._id)}>
            {props.photo.likedBy.includes(user._id) ? 'Unlike' : 'Like'}
          </button>
        )}
      </div>

      <img
        className="card-img"
        src={'http://localhost:3001/' + props.photo.path}
        alt={props.photo.name}
        onClick={() => toggleDetailView()}
      />

      {isDetailView && user && (
        <>
          {/* Comment form */}
          <form onSubmit={handleCommentSubmit}>
            <input
              type="text"
              value={comment}
              onChange={handleCommentChange}
              placeholder="Enter your comment"
            />
            <button type="submit">Add Comment</button>
          </form>
        </>
      )}
      {isDetailView &&
      
      <div className="comments">
          <Comments photoId={props.photo._id} />

          </div>
      
      }
    </div>
  );
}

export default Photo;
