import React, { useState, useContext, useEffect } from 'react';
import Comment from './Comment';
import { UserContext } from '../userContext';

function Photo(props) {
  const [comment, setComment] = useState('');
  const [isDetailView, setIsDetailView] = useState(false);
  const userContext = useContext(UserContext); 
  const [photoData, setPhotoData] = useState([]);

  const { user } = userContext;

  useEffect(() => {
    // Fetch the updated photo data after liking
    const fetchUpdatedPhotoData = async () => {
      try {
        const res = await fetch(`http://localhost:3001/photos/${props.photo._id}`);
        console.log(props.photo._id);
        console.log(props.photo._id);
        const updatedPhoto = await res.json();
        setPhotoData(updatedPhoto);
      } catch (error) {
        console.error('Error fetching updated photo data:', error);
      }
    };

    fetchUpdatedPhotoData();
  }, [props.photo._id, photoData.likes]) // Trigger the effect when the photo ID changes


  const handleLike = async (photoId) => {
    try {
      // Send the like request to the server
      const res = await fetch(`http://localhost:3001/photos/${photoId}/like`, {
        method: 'POST',
        credentials: 'include',
      });
      const updatedPhoto = await res.json();
      
      // The effect will be triggered and update the photo data
    } catch (error) {
      console.error('Error liking photo:', error);
    }
  };
  
  const handleCommentChange = (event) => {
    setComment(event.target.value);
  };
  
  const toggleDetailView = () => {
    setIsDetailView(!isDetailView);
  };

  const handleCommentSubmit = async (event) => {
    event.preventDefault();

    if (!user) {
      console.log("User not logged in");
      return;
    }

    if (!comment) {
      console.log("Comment field cannot be empty");
      return;
    }

    // Make API request to create a new comment
    const res = await fetch('http://localhost:3001/comments', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        photoId: props.photo._id,
        content: comment,
        postedBy: user._id,
      }),
    });

    if (res.ok) {
      // Comment created successfully, perform any necessary actions
      // such as updating the comments list or refreshing the photo display
      // ...

      // Reset the comment input field
      setComment('');
    } else {
      console.log("Error: Comment creation failed");
      // Handle error if the comment creation fails
      // ...
    }
  };

  const publishTime = new Date(props.photo.publishTime).toLocaleString([], {
    year: 'numeric',
    month: 'short',
    day: 'numeric',
    hour: '2-digit',
    minute: '2-digit',
    second: '2-digit',
  });

  return (
    <div className="card text-dark mb-4">
      <div className="card-body">
        <h5 className="card-title">Title: {photoData.name}</h5>
        <p className="card-text">Posted by: {props.photo.postedBy.username}</p>
        <p className="card-text">Published at: {publishTime}</p>
        <p className="card-text">Likes: {photoData.likes}</p>
        <button onClick={() => handleLike(props.photo._id)}>
  {userContext.user && props.photo.likedBy.includes(userContext.user._id)
    ? 'Unlike'
    : 'Like'}
</button>

      </div>
      <img
        className="card-img"
        src={"http://localhost:3001/" + props.photo.path}
        alt={props.photo.name}
        onClick={() => toggleDetailView()}
      />

      {isDetailView && (
        <>
          {/* Comment form */}
          <form onSubmit={handleCommentSubmit}>
            <input
              type="text"
              value={comment}
              onChange={handleCommentChange}
              placeholder="Enter your comment"
            />
            <button type="submit">Add Comment</button>
          </form>

          {/* Render comments */}
          <div className="comments">
            {props.photo.comments.map((comment) => (
              <Comment key={comment._id} comment={comment} />
            ))}
          </div>
        </>
      )}
    </div>
  );
}

export default Photo;
