import React, { useState, useEffect } from 'react';
import Listing from './Listing';
import Photo from './Photo';
import TagSearch from './TagSearch';

function Photos() {
  const [listings, setListings] = useState([]);
  const [searchResults, setSearchResults] = useState([]);
  const [filteredListings, setFilteredListings] = useState([]);

  useEffect(() => {
    const fetchListings = async () => {
      try {
        const res = await fetch('http://localhost:3001/listings');
        const data = await res.json();
        const filteredData = data.filter(listing => listing.dislikes < 2);
        setListings(filteredData); // Set listings that have less than 2 dislikes
        setFilteredListings(filteredData);
      } catch (error) {
        console.error('Error fetching listings:', error);
      }
    };
    fetchListings();
  }, []);

  const handleTagSearch = (searchTerm) => {
    // Filter the listings based on the search term
    if (searchTerm.trim() === '') {
      setSearchResults([]); // Empty search term, show all listings
    } else {
      const results = listings.filter(
        (listing) => listing.photos && listing.photos.some(photo => photo.tags && photo.tags.includes(searchTerm.toLowerCase()))
      );
      setSearchResults(results);
    }
  };

  const listingsToRender = searchResults.length > 0 ? searchResults : filteredListings;

  return (
    <div>
      <h3>Listings:</h3>
      <ul>
        {listingsToRender.map((listing) => (
          <Listing listing={listing} key={listing._id} />
        ))}
      </ul>
    </div>
  );
}

export default Photos;
