import { useState, useEffect } from 'react';
import Photo from './Photo';
import TagSearch from './TagSearch';

function Photos(){
    const [photos, setPhotos] = useState([]);
    const [searchResults, setSearchResults] = useState([]);


    const handleTagSearch = (searchTerm) => {
        // Filter the photos based on the search term
        const results = photos.filter((photo) =>
          photo.tags.includes(searchTerm.toLowerCase())
        );
        setSearchResults(results);
      };
    
    useEffect(function(){
        const getPhotos = async function(){
            const res = await fetch("http://localhost:3001/photos");
            const data = await res.json();
            setPhotos(data);
        }
        getPhotos();
    }, []);

    return(
        <div>
            <h3>Photos:</h3>
                <TagSearch onSearch={handleTagSearch} />

            <ul>
                {photos.map(photo=>(<Photo photo={photo} key={photo._id}></Photo>))}
            </ul>
        </div>
    );
}

export default Photos;
