import React, { useState } from 'react';
import Comment from './Comment';
import { UserContext } from '../userContext';

function Photo(props) {
    
  const [comment, setComment] = useState('');
  const [user, setUser] = useState(null);
  const [isDetailView, setIsDetailView] = useState(false);

  const handleCommentChange = (event) => {
    setComment(event.target.value);
  };
  const toggleDetailView = () => {
    setIsDetailView(!isDetailView);
  };

  const handleCommentSubmit = async (event) => {
    event.preventDefault();


    if (!userId) {
        console.log(userId);
        // User is not logged in, handle accordingly (e.g., show a login prompt)
        return;
    }
    
    if (!comment) {
          console.log("ups2");
        // Comment field is empty, handle accordingly (e.g., show an error message)
        return;
      }
    // Make API request to create a new comment
    const res = await fetch('http://localhost:3001/comments', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        photoId: props.photo._id,
        content: comment,
        postedBy:  userId
      }),
    });

    if (res.ok) {
      // Comment created successfully, perform any necessary actions
      // such as updating the comments list or refreshing the photo display
      // ...

      // Reset the comment input field
      setComment('');
    } else {
      // Handle error if the comment creation fails
      // ...
    }
  };

  const publishTime = new Date(props.photo.publishTime).toLocaleString([], {
    year: 'numeric',
    month: 'short',
    day: 'numeric',
    hour: '2-digit',
    minute: '2-digit',
    second: '2-digit',
  });

  const handleLikeClick = async () => {
    // Make API request to like/unlike the photo
    const res = await fetch(`http://localhost:3001/photos/${props.photo._id}/like`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        userId: user._id,
      }),
    });

    if (res.ok) {
      // Like/unlike successful, perform any necessary actions
      // such as updating the number of likes or refreshing the photo display
      // ...
    } else {
      // Handle error if the like/unlike fails
      // ...
    }
  };



  return (
    <div className="card text-dark mb-4">
      <div className="card-body">
        <h5 className="card-title">Title: {props.photo.name}</h5>
        <p className="card-text">Posted by: {props.photo.postedBy.username}</p>
        <p className="card-text">Published at: {publishTime}</p>
        <p className="card-text">Likes: {props.photo.likes}</p>
        <button onClick={handleLikeClick}>{props.photo.likedBy.includes(userId) ? 'Unlike' : 'Like'}</button>

      </div>
      <img className="card-img" src={"http://localhost:3001/" + props.photo.path} 
      alt={props.photo.name}  onClick={() => toggleDetailView()}  />

      {isDetailView && (
      <>
        {/* Comment form */}
        <form onSubmit={handleCommentSubmit}>
          <input
            type="text"
            value={comment}
            onChange={handleCommentChange}
            placeholder="Enter your comment"
          />
          <button type="submit">Add Comment</button>
        </form>

        {/* Render comments */}
        <div className="comments">
          {props.photo.comments.map((comment) => (
            <Comment key={comment._id} comment={comment} />
          ))}
        </div>
      </>
    )}
    </div>
  );
}

export default Photo;
