import React, { useState, useEffect, useContext } from 'react';
import { UserContext } from './userContext';
import ListingShow from './ListingShow';

function Index() {
  const [listings, setListings] = useState([]);
  const userContext = useContext(UserContext);
  const { user } = userContext;

  useEffect(() => {
    const fetchListings = async () => {
      try {
        const response = await fetch('http://localhost:3001/listings');
        if (response.ok) {
          const data = await response.json();
          setListings(data); // Update the listings state with the fetched data
        } else {
          console.error('Failed to fetch listings:', response.status);
          // Handle the error or show an error message to the user
        }
      } catch (error) {
        console.error('Error fetching listings:', error);
        // Handle the error or show an error message to the user
      }
    };

    fetchListings();
  }, []);

  const handleBookListing = async (listingId) => {
    try {
      const response = await fetch(`http://localhost:3001/listings/${listingId}/book`, {
        method: 'POST',
        credentials: 'include',
      });

      if (response.ok) {
        // Update the listings state to remove the booked listing
        setListings((prevListings) => prevListings.filter((listing) => listing._id !== listingId));
      } else {
        console.error('Error booking listing:', response.status);
        // Handle the error or show an error message to the user
      }
    } catch (error) {
      console.error('Error booking listing:', error);
      // Handle the error or show an error message to the user
    }
  };

  const handleTakeDownListing = async (listingId) => {
    try {
      const response = await fetch(`http://localhost:3001/listings/${listingId}/take-down`, {
        method: 'POST',
        credentials: 'include',
      });

      if (response.ok) {
        // Update the listings state to remove the taken down listing
        setListings((prevListings) => prevListings.filter((listing) => listing._id !== listingId));
      } else {
        console.error('Error taking down listing:', response.status);
        // Handle the error or show an error message to the user
      }
    } catch (error) {
      console.error('Error taking down listing:', error);
      // Handle the error or show an error message to the user
    }
  };

  return (
    <div>
      <h3>Listings:</h3>
      <ul>
        {listings.map((listing) => (
          <ListingShow
            listing={listing}
            key={listing._id}
            onBook={() => handleBookListing(listing._id)}
            onTakeDown={() => handleTakeDownListing(listing._id)}
            currentUser={user}
          />
        ))}
      </ul>
    </div>
  );
}

export default Index;
