import React, { useState, useEffect } from 'react';

function ListingShow({ listing, currentUser }) {
  const [photos, setPhotos] = useState([]);

  useEffect(() => {
    const fetchListingPhotos = async (listingId) => {
      try {
        const response = await fetch(`http://localhost:3001/listings/${listing._id}/photos`, {
          method: 'GET',
          credentials: 'include', // Include cookies in the request
        });        if (response.ok) {
          const photosData = await response.json();
          setPhotos(photosData);
        } else {
          console.error('Failed to fetch listing photos:', response.status);
          // Handle the error or show an error message to the user
        }
      } catch (error) {
        console.error('Error fetching listing photos:', error);
        // Handle the error or show an error message to the user
      }
    };

    fetchListingPhotos(listing._id);
  }, [listing]);

  const handleBookListing = async () => {
    try {
      const response = await fetch(`http://localhost:3001/listings/${listing._id}/book`, {
        method: 'POST',
        credentials: 'include', // Include cookies in the request
      });

      if (response.ok) {
        // Listing booked successfully
        // Perform any necessary actions, such as updating the UI
      } else {
        // Handle the error or show an error message to the user
        console.error('Failed to book the listing:', response.status);
      }
    } catch (error) {
      console.error('Error booking the listing:', error);
      // Handle the error or show an error message to the user
    }
  };

  const handleTakeDownListing = async () => {
    try {
      const response = await fetch(`http://localhost:3001/listings/${listing._id}/delete`, {
        method: 'POST',
        credentials: 'include', // Include cookies in the request
      });

      if (response.ok) {
        // Listing taken down successfully
        // Perform any necessary actions, such as updating the UI
      } else {
        // Handle the error or show an error message to the user
        console.error('Failed to take down the listing:', response.status);
      }
    } catch (error) {
      console.error('Error taking down the listing:', error);
      // Handle the error or show an error message to the user
    }
  };

  return (
    <div className="card">
      <div className="card-body">
        <h4 className="card-title">Title: {listing.title}</h4>
        <p className="card-text">Location: {listing.location}</p>
        {photos.length > 0 && (
          <div>
            <h4>Photos:</h4>

      <img  src={"http://localhost:3001/images/"+listing.photo.photoPath}></img>

          </div>
        )}
        {currentUser && currentUser._id !== listing.postedBy && !listing.booked && (
          <button className="btn btn-primary" onClick={() => handleBookListing(listing._id)}>Book Listing</button>
        )}
        {currentUser && currentUser._id === listing.postedBy && (
          <button className="btn btn-danger" onClick={() => handleTakeDownListing(listing._id)}>Take Down Listing</button>
        )}
      </div>
    </div>
  );
}

export default ListingShow;
