import { useState, useEffect } from 'react';

function DetailedPhoto({ photo, updateSelectedPhoto }) {
  const [comments, setComments] = useState([]);
  const [newComment, setNewComment] = useState('');

  const fetchComments = async () => {
    try {
      const res = await fetch(`http://localhost:3001/photos/${photo._id}/comments`);
      const data = await res.json();
      setComments(data);
    } catch (error) {
      console.log('Error:', error);
    }
  };

  useEffect(() => {
    fetchComments();
  }, [photo]);

  const handleVote = async () => {
    try {
      const res = await fetch(`http://localhost:3001/photos/${photo._id}/vote`, {
        method: 'POST',
      });

      if (res.ok) {
        const updatedPhoto = { ...photo, likes: photo.likes + 1 };
        updateSelectedPhoto(updatedPhoto);
      } else {
        console.log('Failed to vote');
      }
    } catch (error) {
      console.log('Error:', error);
    }
  };

  const handleSubmitComment = async (e) => {
    e.preventDefault();

    try {
      const res = await fetch(`http://localhost:3001/photos/${photo._id}/comments`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ comment: newComment }),
      });

      if (res.ok) {
        setNewComment('');
        fetchComments();
      } else {
        console.log('Failed to submit comment');
      }
    } catch (error) {
      console.log('Error:', error);
    }
  };

  return (
    <div>
      <h2>{photo.title}</h2>
      <img src={photo.imageUrl} alt={photo.title} />
      <p>Posted by: {photo.user}</p>
      <p>Likes: {photo.likes}</p>
      <button onClick={handleVote}>Vote</button>
      <h4>Comments:</h4>
      <ul>
        {comments.map((comment) => (
          <li key={comment._id}>{comment.text}</li>
        ))}
      </ul>
      <form onSubmit={handleSubmitComment}>
        <input
          type="text"
          value={newComment}
          onChange={(e) => setNewComment(e.target.value)}
          placeholder="Add a comment"
        />
        <button type="submit">Submit Comment</button>
      </form>
    </div>
  );
}

export default DetailedPhoto;
