import { useState, useEffect } from 'react';

function Photo(props) {
  const [likes, setLikes] = useState(props.photo.likes);
  const [isLiked, setIsLiked] = useState(false);

  useEffect(() => {
    setIsLiked(props.photo.likedBy.includes(props.currentUser));
  }, [props.currentUser, props.photo.likedBy]);

  const handleLike = async () => {
    if (!isLiked) {
      try {
        const res = await fetch(`http://localhost:3001/photos/${props.photo._id}/like`, {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json'
          },
          body: JSON.stringify({ userId: props.currentUser })
        });

        if (res.ok) {
          setLikes(likes + 1);
          setIsLiked(true);
        } else {
          console.log('Failed to update like status.');
        }
      } catch (error) {
        console.log('Error:', error);
      }
    }
  };

  const publishTime = new Date(props.photo.publishTime).toLocaleString([], {
    year: 'numeric',
    month: 'short',
    day: 'numeric',
    hour: '2-digit',
    minute: '2-digit',
    second: '2-digit'
  });

  return (
    <div className="card text-dark mb-4">
      <div className="card-body">
        <h5 className="card-title">Title: {props.photo.name}</h5>
        <p className="card-text">Posted by: {props.photo.postedBy.username}</p>
        <p className="card-text">Published at: {publishTime}</p>
        <p className="card-text">Likes: {likes}</p>
        <button onClick={handleLike} disabled={isLiked}>
          {isLiked ? 'Liked' : 'Like'}
        </button>
      </div>
      <img className="card-img" src={"http://localhost:3001/" + props.photo.path} alt={props.photo.name} />
    </div>
  );
}

export default Photo;
