import React, { useState, useEffect } from 'react';

function ListingShow({ listing }) {
  const [photos, setPhotos] = useState([]);

  useEffect(() => {
    const fetchListingPhotos = async (listingId) => {
      try {
        const response = await fetch(`http://localhost:3001/listings/${listingId}/photos`);
        if (response.ok) {
          const photosData = await response.json();
          setPhotos(photosData);
        } else {
          console.error('Failed to fetch listing photos:', response.status);
          // Handle the error or show an error message to the user
        }
      } catch (error) {
        console.error('Error fetching listing photos:', error);
        // Handle the error or show an error message to the user
      }
    };

    fetchListingPhotos(listing._id);
  }, [listing]);

  console.log(listing._id);
  return (
    <div>
      <h4>Title: {listing.title}</h4>
      <p>Location: {listing.location}</p>
      {photos.length > 0 && (
        <div>
          <h4>Photos:</h4>
          {photos.map((photo) => (
            <img src={photo.photoPath} alt="Listing Photo" key={photo._id} />
          ))}
        </div>
      )}
    </div>
  );
}

export default ListingShow;
function Photos(){
  const [photos, setPhotos] = useState([]);
  useEffect(function(){
      const getPhotos = async function(){
          const res = await fetch("http://localhost:3001/photos");
          const data = await res.json();
          setPhotos(data);
      }
      getPhotos();
  }, []);

  return(
      <div>
          <h3>Photos:</h3>
          <ul>
              {photos.map(photo=>(<Photo photo={photo} key={photo._id}></Photo>))}
          </ul>
      </div>
  );
}
